import React, {Component} from 'react';
import { feature } from 'topojson-client';
import axios from 'axios';
import { geoKavrayskiy7 } from 'd3-geo-projection';
import { geoGraticule, geoPath } from 'd3-geo';
import { select as d3Select } from 'd3-selection';

import {
    WORLD_MAP_URL,
    SATELLITE_POSITION_URL,
    SAT_API_KEY
} from "../constants";



const width = 960;
const height = 600;

class WorldMap extends Component {
    constructor(){
        super();
        this.state = {
            map: null
        }
        this.map = null;
        this.refMap = React.createRef();
    }

    componentDidMount() {
        axios.get(WORLD_MAP_URL)
            .then(res => {
                const { data } = res;
                const land = feature(data, data.objects.countries).features;
                this.generateMap(land);
            })
            .catch(e => console.log('err in fecth world map data ', e))
    }

    generateMap(land) {
        const projection = geoKavrayskiy7()
            .scale(170)
            .translate([width / 2, height / 2])
            .precision(.1)

        // console.log(projection)
        // map
        const canvas = d3Select(this.refMap.current)
            .attr("width", width)
            .attr("height", height)

        // satellite track
        const canvas2 = d3Select(this.refTrack.current)
            .attr("width", width)
            .attr("height", height)


        let context = canvas.node().getContext("2d");
        let context2 = canvas2.node().getContext("2d");

        const graticule = geoGraticule();
        let path = geoPath().projection(projection).context(context);

        land.forEach( ele => {
            // country
            context.fillStyle = '#B3DDEF';
            context.strokeStyle = '#000';
            context.globalAlpha = 0.7;
            context.beginPath();
            path(ele);
            context.fill();
            context.stroke();

            // latitude and longitude (graticule)
            context.strokeStyle = 'rgba(220, 220, 220, 0.1)';
            context.beginPath();
            path(graticule());
            context.lineWidth = 0.1;
            context.stroke();

            // border of the map top and bottom
            context.beginPath();
            context.lineWidth = 0.5;
            path(graticule.outline());
            context.stroke();

        })

    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.setData !== this.props.setData) {
            const {latitude, longitude, elevation, duration} = this.props.observerData;
            const endTime = duration * 60; // speed

            // generate all satellite urls
            const urls = this.props.satData.map( sat => {
                const { satid } = sat;
                const url = `/api/${SATELLITE_POSITION_URL}/${satid}/${latitude}/${longitude}/${elevation}/${endTime}/&apiKey=${SAT_API_KEY}`;

                return axios.get(url);
            });

            axios
                .all(urls)
                //  promise链式调用
                // es 6.0 it can use async and await.
                .then( axios.spread((...args) => {
                    console.log(args);
                    return args.map( item => item.data)
                }))
                .then( res => {
                    console.log(res)
                })
                .catch( err => {
                    console.log('err', err.message)
                })

        }

    }

    render() {
        return (
            <div className = "map-box">
                <canvas className = "map" ref={this.refMap} />
                <canvas className="track" ref={this.refTrack} />

            </div>
        );
    }
}

export default WorldMap;
